a.pgenmed {
    color: #000000;
}
a.pgenmed:hover {
    color: #2b4157;
}
table.pline tbody tr td.row1 {
    background-color: #FFFFFF;
}
input.post,
textarea.post {
    background-color: #fff;
    border: 1px solid #ccc;
    border-radius: 2px;
    padding: 3px 5px;
    outline: none;
}
input.post:focus,
textarea.post:focus {
    border: 1px solid #B5C9DD;
    box-shadow: 0px 0px 10px 0px rgba(50, 50, 50, 0.2);
    transition: All 0.3s cubic-bezier(.11, 0, .02, 1);
}
input,
textarea,
select {
    background-color: #fff;
    border: 1px solid #ccc;
    padding: 2px 5px;
    border-radius: 2px;
    outline: none;
}
input:hover,
textarea:hover,
select:hover {
    border: 1px solid #B5C9DD;
    transition: All 0.3s cubic-bezier(.11, 0, .02, 1);
}
form[name='search_block'] {
    margin-bottom: 10px;
}
span.gen select,
span.gen input {
    padding: 6px 5px;
    border: 0px solid #B5C9DD;
    box-shadow: 0px 1px 1px 0px rgba(50, 50, 50, 0.3);
    transition: All 0.3s cubic-bezier(.11, 0, .02, 1);
}
span.gen select:focus,
span.gen input:focus {
    padding: 6px 5px;
    border: 0px solid #B5C9DD;
    box-shadow: 0px 2px 6px 0px rgba(50, 50, 50, 0.4);
    transition: All 0.3s cubic-bezier(.11, 0, .02, 1);
}
span.gen input.liteoption {
    padding: 7px 40px 8px;
}
div#CGCandy377 {
    display: none;
}
.copyright,
.copyright a {
    color: #D0D0D0 !important;
}
.seed,
.seedmed,
.seedsmall {
    color: #2ECB2E;
}
.leech,
.leechmed,
.leechsmall {
    color: #E42B2B;
}
.forumline {
    background-color: #EEEEEE !important;
    border-collapse: collapse;
}
hr {
    border: solid #EEEEEE;
    border-top-width: 1px;
    border-bottom-width: 0;
}

table.header tbody tr td table tbody tr td a img {
    display: none
}
a.mainmenu {
    width: 200px;
}

td[width='40%'][align='center']:first-of-type {
    text-align: left;
    width: 20%;
}
td[width='40%'][align='center']:nth-last-of-type(1) {
    text-align: right;
    width: 20%;
}
table.header td[width='20%'] {
    width: 60%;
}

table[width='100%'][cellspacing='2'][cellpadding='2']:first-of-type {
    background-color: #fff;
    vertical-align: middle;
    padding: 10px 15px 14px;
    margin: 18px 0px 0px;
    border-radius: 3px 3px 0px 0px;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2);
}
table[width='100%'][cellspacing='2'][cellpadding='2']:first-of-type br {
    display: none;
}
table[width='100%'][cellspacing='2'][cellpadding='2']:nth-of-type(2) {
    background-color: #FFFFFF;
    overflow: hidden;
    padding: 0px 14px;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2);
}
table.forumline[cellpadding='6'] {
    background-color: #FFFFFF;
    border-radius: 0px 0px 3px 3px;
    overflow: hidden;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2);
    margin-bottom: 0;
}
table.forumline[cellpadding='6'] tr,
table.forumline[cellpadding='6'] td {
    background-color: #FFFFFF;
    border: 0px;
}
.row1 {
    background-color: #FFF;
}
.row2 {
    background-color: #FFF;
}
table[style='background-color:#aec9e4;'] {
    background-color: #EEEEEE !important;
    margin: 6px 0px 10px;
}
form[action='search.php?mode=results'] input.hint {
    background-color: #FFF !important;
    color: #C5C5C5;
    border: 0;
}
form[action='search.php?mode=results'] > input,
textarea,
select {
    background-color: #fff;
    border: 0;
    border-radius: 2px;
    padding: 6px 5px;
    box-shadow: 0px 1px 1px 0px rgba(50, 50, 50, 0.3);
    transition: All 0.3s cubic-bezier(.11, 0, .02, 1);
    outline: none;
}
form[action='search.php?mode=results'] > input:focus {
    box-shadow: 0px 2px 6px 0px rgba(50, 50, 50, 0.4);
    transition: All 0.3s cubic-bezier(.11, 0, .02, 1);
}
input[type='submit'][name='search_submit'][value=''] {
    font-weight: bold;
    font-size: smaller;
    color: #f4f8fb;
    background-color: #69c;
    padding: 5px 20px 7px;
}
input[type='submit'][name='search_submit'][value='']:hover {
    background-color: #7BACDC !important;
    color: #fff;
    border: none;
    box-shadow: 0px 2px 6px 0px rgba(50, 50, 50, 0.4);
    transition: All 0.3s cubic-bezier(.11, 0, .02, 1);
    cursor: pointer;
}
th,
.top {
    color: #FFF !important;
    font-size: 9pt;
    background-color: #69c !important;
    background-image: none;
}
td.row1 {
    background-color: #FFFFFF;
    padding: 16px;
}
td.row2 {
    background-color: #FFFFFF;
    padding: 16px;
}
td.spaceRow {
    display: none;
}
.postbody {
    font-size: 13px;
    font-family: tahoma, Arial, Helvetica, sans-serif;
    line-height: 18px;
}
tr.row1 {
    border-bottom: 1px solid #EEE;
}
tr.row2 {
    border-bottom: 1px solid #EEE;
}
b a[rel='nofollow'] {
    background-color: #2ECB2E;
    color: #fff;
    padding: 9px 0px;
    border-radius: 3px;
    display: block;
    width: 90%;
    text-decoration: none;
    box-shadow: 0px 1px 1px 0px rgba(50, 50, 50, 0.3);
    transition: All 0.5s cubic-bezier(.11, 0, .02, 1);
}
b a[rel='nofollow']:hover {
    color: #fff !important;
    background-color: #4DEA4D;
    text-decoration: none;
    box-shadow: 0px 2px 6px 0px rgba(50, 50, 50, 0.4);
    transition: All 0.2s cubic-bezier(.11, 0, .02, 1);
}
.btTbl {
    width: 100%;
    background-color: #EEE;
    border-radius: 3px;
    overflow: hidden;
}
.row3 {
    background-color: #EEEEEE;
}
img[src='images/icon_arrow.gif'],
img[alt='Free Leech'] {
    display: none;
}
.rowgold {
    background-image: none;
    background-color: #FFFABA;
}
.forumline {
    border-radius: 3px;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2);
    margin-bottom: 10px;
    overflow: hidden;
}

.blob-code-inner,
.blob-num,
.highlight pre {
  font-family: "Operator Mono SSm Lig Book" !important;
  font-size: 16px !important;
}
.pl-c,
.pl-e {
  font-style: italic;
}
.pl-c {
  color: #4CAF50;
}

