  img[src*="img-share-achievements.png"] {
    filter: invert(100%) hue-rotate(-85deg) !important;
  }

:root {
    --bg-custom: url(data:image/png;base64,);
    --bg-img: url(https://camo.githubusercontent.com/5b8e4cd1a5ce69b6affa17ebd8e4aee00c147618/68747470733a2f2f7374796c6973687468656d65732e6769746875622e696f2f4769744875622d4461726b2f696d616765732f6261636b67726f756e64732f62672d63726f737368617463682e706e67);
  }
  * {
    box-sizing: border-box;
  }
  body {
    color: #e0e0e0;
    background-color: #222;
    background-image: var(--bg-img);
    background-clip: border-box;
    background-origin: padding-box;
    background-attachment: scroll;
    background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important;
  }
  .markdown-body img {
    background-color: #181818;
    background: none;
  }
  a {
    color: rgb(79, 140, 201);
    background-color: transparent;
  }
  .markdown-body h1, .markdown-body h2, .markdown-body h3, .markdown-body h4,
  .markdown-body h5, .markdown-body h6 {
    border-bottom: 1px solid #444;
  }
  .border-gray-light, hr {
    border-color: #444 !important;
  }
  .markdown-body table th, .markdown-body table td {
    border: 1px solid #444;
  }
  .text-gray {
    color: #aaa !important;
  }
  .markdown-body table tr {
    background-color: #181818;
    border-top: 1px solid #444;
  }
  .markdown-body table tr:nth-child(2n) {
    background-color: #202020;
  }
  /* style 404 page e.g. http://stylishthemes.github.io/ */
  p {
    color: #bebebe;
  }
  #suggestions a {
    color: #9e9e9e;
  }

