@-moz-document regexp("^https?://((chat|meta|es|ja|pt|ru)\\.)*stackoverflow\\.com.*"),
domain("serverfault.com"),
domain("superuser.com"),
domain("stackapps.com"),
domain("mathoverflow.net"),
domain("askubuntu.com"),
regexp("^https?:\/\/((?!area51)\w+\.)*?stackexchange\.com.*") {
  /**
   * ** Will apply to almost all Stack Exchange Sites **
   * Except:
   * - Area 51 (area51.stackexchange.com)
   * ** Please open an issue to report sites where the style breaks **
   * https://github.com/StylishThemes/StackOverflow-Dark/issues
   * Note: fix borders by removing single glob color; some have been fixed.
	 *       if you notice any broken broders please open a new issue with page link.
   */
  * {
    /* dont assign any colors here */
    text-shadow: none !important;
    box-shadow: none !important;
  }
  body, .cv-story header {
    background-color: #191919 !important;
    background-image: /*[[bg-choice]]*/ !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: /*[[bg-attachment]]*/ !important;
    /*[[bg-options]]*/
  }
  .subheader a {
    border-color: inherit !important;
  }
  body {
    color: #ccc;
  }
  /* custom code font */
  pre.prettyprint, code {
    font-family: "/*[[font-choice]]*/",
      Monaco,
      Consolas,
      "Liberation Mono",
      Menlo,
      Courier,
      monospace !important;
  }
  .snippet-code {
    padding: 5px !important;
  }
  /* fix offset between default style and dark */
  .post-text pre.prettyprint {
    margin-left: 0 !important;
  }
  /* === custom color === */
  a, a:hover, a:active, .profile-popup a, .seWrapper a, .gs-title b,
  .spoiler:hover a, .comment-up-on, .comment-up-off:hover,
  #side-menu ul .category, .s-timeline .-timestamp, .mod-flair, .s-btn,
  .s-btn:hover, .s-stat .-value, .s-link, .mini-counts.supernova, .supernova,
  .topbar-dialog a:not(.btn-secondary):not(.nav-links--link),
  .topbar-dialog a:not(.btn-secondary):not(.nav-links--link):hover,
  .s-sidebarwidget--action, .site-footer .-link._active, ul.navMain li a,
  ul.navMain li a:hover, .top-bar .-secondary .-ctas .btn-topbar-clear,
  .top-bar .-secondary .-ctas .btn-topbar-clear,
  .top-bar .-secondary .-ctas .btn-topbar-clear:hover,
  .top-bar .-secondary .-ctas .btn-topbar-clear:focus,
  .topbar-dialog a:not(.btn-secondary):not(.nav-links--link):visited,
  .topbar-dialog a:not(.btn-secondary):not(.nav-links--link):visited:hover,
  .user-details h1 a, .default-profile-site a, .answer-hyperlink,
  .answer-hyperlink:hover, .topbar-dialog .unread-item .unread-bold,
  .started .mod-flair, .started a:not(.started-link) {
    color: /*[[base-color]]*/;
  }
  .subheader h1 a, input[type="submit"].btn-clear,
  input[type="button"].btn-clear, button.btn-clear, .btn-clear,
  html:not(#X) .p-fc-purple, html:not(#X) .p-ff-roboto-slab.p-fc-purple div,
  .user-page .subheader.reloaded .additional-links a[href], .fc-blue-600,
  .h\:fc-orange-500:hover, #company-page .s-btn__outlined:hover,
  #company-page .s-btn__outlined:active, h2 a.league-site-name,
  .summary h3 .question-hyperlink, a.realtime-question-url.realtime-title,
  a.question-link, a.question-hyperlink, a.s-link,
  .s-anchors.s-anchors__default a:not(.s-link),
  .s-anchors .s-anchors.s-anchors__default a:not(.s-link), a code,
  .company-list h2 a[href],
  #featured-company-page-ad footer a.s-btn__outlined:hover,
  #featured-company-page-adfooter a.s-btn__outlined:active,
  .topbar-dialog .modal-content .message-text h4 {
    color: /*[[base-color]]*/ !important;
  }
  .search-controls-responsive .-item .s-btn__outlined, .s-btn__outlined {
    border-color: /*[[base-color]]*/ !important;
    color: /*[[base-color]]*/ !important;
  }
  .search-form.responsive .filter-pane nav ul._tabs li.tab [data-tab] a .filter-indicator {
    color: #eee !important;
    background-color: /*[[base-color]]*/ !important;
    border-color: #eee;
  }
  .s-sidebarwidget--item[aria-current="true"]:before,
  .s-sidebarwidget--item > :first-child[aria-current="true"]:before,
  .s-sidebarwidget--item[aria-current="page"]:before,
  .s-sidebarwidget--item > :first-child[aria-current="page"]:before,
  .topbar-dialog.siteSwitcher-dialog .current-site-container, .s-btn__primary {
    border-color: /*[[base-color]]*/ !important;
  }
  .top-bar {
    border-top-color: /*[[base-color]]*/;
  }
  html .message.message-info .message-tip-left-top:before {
    border-top-color: /*[[base-color]]*/ !important;
  }
  .message.message-info .message-tip::before {
    border-color: transparent !important;
  }
  .nav .primarynav li a:hover, .message.message-config .actions .button,
  .btn-careers:hover, .btn-careers:visited, .question-list-layout a:hover {
    color: #eee !important;
    background: /*[[base-color]]*/ !important;
  }
  .s-timeline .-event::before {
    background: #ccc;
    border: 4px solid #666;
  }
  .s-timeline::after, .s-timeline .-period:last-child .-event:last-child::after {
    background: #666;
  }
  .search-form .search-inputs .-item .iconSearch,
  .search-form .search-inputs .-item .iconLocation {
    opacity: .8;
    color: #ddd;
  }
  #sub-navs a:hover .messages-icon {
    opacity: 1;
  }
  #sub-navs .menu-item:hover .settings-icon {
    filter: brightness(135%);
  }
  /* these must be here else they dont work */
  input[class="w100"][type="submit"] {
    background: #622 !important;
    border: 1px solid #ff4a15 !important;
  }
  input[class="w100"][type="submit"]:hover {
    background: #622 !important;
    border: 1px solid #ff4a15 !important;
    filter: brightness(90%) !important;
  }
  .delete-tag, .icon-i, .icon-i-blue, .icon-i-orange {
    filter: brightness(70%) contrast(170%) !important;
  }
  .why-jobs-v2 .developers-first input[type="submit"] {
    background: #222 !important;
    color: /*[[base-color]]*/ !important;
  }
  .min-ds-step-counter .step.active {
    background: /*[[base-color]]*/;
  }
  input[type="submit"].btn-clear:hover, input[type="button"].btn-clear:hover,
  button.btn-clear:hover, .btn-clear:hover, input[type="submit"].btn-clear:focus,
  input[type="button"].btn-clear:focus, button.btn-clear:focus, .btn-clear:focus,
  .s-btn__outlined:not(.youarehere):hover,
  .s-btn__outlined:not(.youarehere):active {
    color: /*[[base-color]]*/ !important;
    background-color: rgba(/*[[base-color-rgb]]*/, .2) !important;
  }
  input[type="submit"]:not(.s-btn), input[type="button"]:not(.s-btn),
  button:not(.s-btn), .button, a.button, a.button:visited, .btn,
  .hero-box.double-panel .panel.white .btn {
    color: #eee;
    background-color: /*[[base-color]]*/;
    border-color: rgba(/*[[base-color-rgb]]*/, .5);
    box-shadow: none;
  }
  .about-newuser .huge-button {
    background: none !important;
    background-color: /*[[base-color]]*/ !important;
    border-color: rgba(/*[[base-color-rgb]]*/, .5) !important;
  }
  .old-hero {
    color: #eee;
    background-color: rgba(/*[[base-color-rgb]]*/, .4);
    background-image: linear-gradient(to right, transparent 0%, transparent 100%);
  }
  a.owner {
    background-color: rgba(/*[[base-color-rgb]]*/, .3);
  }
  input[type="submit"]:not(.s-btn):hover, input[type="button"]:not(.s-btn):hover,
  button:not(.s-btn):hover, .button:hover, a.button:hover,
  a.button:visited:hover, .btn:hover, input[type="submit"]:not(.s-btn):focus,
  input[data-push="true"][type="button"]:not(.s-btn):focus,
  button:not(.s-btn):focus, .button:focus, a.button:focus,
  a.button:visited:focus, .btn:focus {
    background-color: /*[[base-color]]*/;
    border-color: rgba(/*[[base-color-rgb]]*/, .5);
    filter: brightness(120%) !important;
  }
  a.post-tag:hover, .post-tag:hover, .bounty-indicator, .bounty-indicator-tab,
  .bounty-award, span.bounty-indicator-tab, #change-picture, .supernovabg,
  .hotbg, .nav li.youarehere a, .nav a:hover, a.button, .edit-count,
  .pager a:hover span:not(.page-numbers), .bounties-tab,
  #sidebar a[href*="/questions/ask"], #sidebar a[href*="/questions/ask"]:visited,
  .s-btn.s-btn__primary, .s-btn.s-btn__primary:focus,
  .top-bar .-secondary .-ctas .btn-topbar-primary,
  .top-bar .-secondary .-ctas .btn-topbar-primary:focus,
  .user-card-name .top-badge, .user-card-name .top-badge a {
    background-color: /*[[base-color]]*/ !important;
    color: #eee !important;
  }
  .privileges-page .privilege-table-row:hover, .top-bar .my-profile:hover,
  .top-bar .-secondary .-item .-link:hover {
    background-color: #242424;
  }
  .privileges-page .privilege-table-row:hover .long-description,
  .faq-page #mainbar .content-page p a:hover, a.-link:hover, h3 strong a:hover,
  ._icon p a:hover, .s-link:hover, .s-btn__link:hover {
    color: /*[[base-color]]*/;
    filter: brightness(115%);
  }
  .old-hero a.-link, .fc-black-900 {
