  }
  .eoy--2018 .eoy-hero, .eoy .eoy-cheers {
    background-color: var(--mid-gray-two);
  }
  .eoy--2018.eoy .eoy-publisher-link h3:hover {
    color: var(--dark-gray);
  }
  /* blog */
  #sidebar .widget-title, .sidebar_content .post-title {
