    background-color: var(--black);
  }
  .sidebar_content #content, .sidebar_content .content_section {
    color: var(--icon-gray);
    background-color: var(--mid-gray);
  }
  .integration h3 {
    color: var(--light-gray-two);
  }
}

sections.code:
:root {
    --light-gray: #888;
    --light-gray-two: #777;
    --mid-gray: #222;
    --mid-gray-two: #333;
    --dark-gray: #505050;
    --dark-gray-two: #444;
    --dark-gray-three: #666;
    --icon-gray: #919191;
    --icon-gray-two: #989797;
    --mid-gray-three: #b2b2b2;
    --off-black: #181818;
    --black: #111;
    --really-black: #080808;
    --white: #eee;
    --deep-green: #007a73;
    --sky-blue: #1eabf9;
    --med-blue: #4885ed;
    --weird-blue: #478f8f;
    --light-blue: #5dcfca;
    --btn-red-one: #d3505a;
    --btn-red-two: #ee5f64;
    --btn-red-three: #d13644;
    --dark-red: #300;
    --light-red: #f33;
    --red: #f00;
  }
  /* front page */
  .mktg-bg--l-gray {
    background-color: var(--light-gray);
  }
  .mktg-bg--m-gray {
    background-color: var(--mid-gray);
  }
  .mktg-bg--deep-green {
    background-color: var(--deep-green);
  }
  .mktg-bg--white {
    background-color: var(--white);
  }
  .mktg-container.mktg-bg--dancer {
    background-image: linear-gradient(90deg, rgba(0, 0, 0, .1) 0, rgba(0, 0, 0, .5)),
                          url("https://assets.getpocket.com/web/main/Components/HomePage/dancer.55decab57ea433e9f70fb55f52fd8542.png");
  }
  @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
    .mktg-container.mktg-bg--dancer {
      background-image: linear-gradient(90deg, rgba(0, 0, 0, .1) 0, rgba(0, 0, 0, .5)),
                              url("https://assets.getpocket.com/web/main/Components/HomePage/dancer@2x.3668c6f32d26a1013690213e6e7b86ae.png");
    }
  }
  .mktg-icon--gray path {
    fill: var(--icon-gray);
  }
  .mktg-icon--white path {
    fill: var(--white);
  }
  .home-page .mktg-homepage-signin {
    border: 1px solid var(--mid-gray-three);
  }
  .home-page .mktg-homepage-signin, .mktg-panel {
    color: var(--white);
  }
  .mktg-panel {
    min-width: var(--min-width);
  }
  .mktg-panel.mktg-panel--dark, .mktg-panel.mktg-panel--dark .mktg-panel__title {
    color: var(--white);
  }
  .mktg-panel__title {
    color: var(--white);
  }
  .mktg-button {
    background-color: var(--mid-gray);
    color: var(--white);
  }
  .mktg-button:hover {
    color: var(--white);
  }
  .mktg-button.mktg-button--cta {
    background-color: var(--sky-blue);
    color: var(--white);
  }
  .mktg-button.mktg-button--cta-dark {
    background-color: var(--med-blue);
    color: var(--white);
  }
  .mktg-button--cta-dark:hover, .mktg-button--cta:hover {
