  #fullArticle .private-note, .category-list .category, .modal {
    background-color: var(--mid-gray);
  }
  #sidebar .nav-list a:hover, #sidebar .nav-list a:focus {
    color: var(--dark-gray);
  }
  #sidebar .nav-list .active a, #sidebar .nav-list .active a:hover,
  #sidebar .nav-list .active a:focus {
    color: var(--mid-gray-two);
  }
  .category-list .category {
    border-color: var(--mid-gray-two);
  }
  .category-list .category:hover {
    background-color: var(--dark-gray-two);
  }
  .category-list .category:hover h3 {
    color: var(--white);
  }
  #docsSearch {
    background: var(--mid-gray-two);
    border-bottom: 1px solid var(--mid-gray-two);
  }
  #contactModal h2, .abuse h2 {
    background-color: var(--black);
  }
  /* search boxes */
  .explore_search input {
    border-color: var(--icon-gray);
  }
  .explore_search input, .explore_search.search_toolbar input,
  #sidebar form .search-query {
    color: var(--white);
    background-color: var(--black);
  }
  #serp-dd {
    border-color: var(--dark-gray);
  }
  #serp-dd .result {
    color: var(--icon-gray);
    background-color: var(--mid-gray);
  }
  #serp-dd .result > li.active, #serp-dd .result a:hover {
    background-color: var(--mid-gray-two);
  }
  select, textarea, input[type="text"], input[type="password"],
  input[type="datetime"], input[type="datetime-local"], input[type="date"],
  input[type="month"], input[type="time"], input[type="week"],
  input[type="number"], input[type="email"], input[type="url"],
  input[type="search"], input[type="tel"], input[type="color"],
  .uneditable-input, #sidebar form .search-query {
    color: var(--icon-gray);
    background-color: var(--black);
    border: 1px solid var(--mid-gray-two);
  }
  textarea:focus, input:focus, input[type="text"]:focus,
  input[type="password"]:focus, input[type="datetime"]:focus,
  input[type="datetime-local"]:focus, input[type="date"]:focus,
  input[type="month"]:focus, input[type="time"]:focus, input[type="week"]:focus,
  input[type="number"]:focus, input[type="email"]:focus, input[type="url"]:focus,
  input[type="search"]:focus, input[type="tel"]:focus, input[type="color"]:focus,
  .uneditable-input:focus, #sidebar form .search-query:focus {
    color: var(--white) !important;
    border-color: var(--med-blue) !important;
    opacity: .6;
  }
  .most-pop-articles .popArticles li a {
    color: var(--mid-gray-three);
  }
  /* Premium page */
  .premium-form > form {
    background-color: var(--mid-gray);
    border: none;
  }
  .page-premium .premium-form .premium-divider, .premium-tabledetail .row,
  .page-premium .premium-divider {
    border-color: var(--dark-gray-three);
  }
  .page-premium .premium-form .premium-divider > h5 {
    background-color: var(--mid-gray);
  }
  .premium-form .toggle-yrmth {
    background-color: var(--mid-gray-two);
  }
  .premium-form .toggle-yrmth-selected {
    background-color: var(--dark-gray);
  }
  .premium-form .toggle-yrmth:hover, .premium-form .toggle-yrmth-selected:hover {
    background-color: var(--black);
  }
  .premiumform-container .form-field label {
    color: var(--light-gray-two);
  }
  .page-premium .premium-features h4 {
    color: var(--white);
  }
  .page-premium .premium-form, .page-premium .premium-divider > h5 {
    background-color: var(--black);
  }
  .premium-tabledetail p, .premium-tabledetail h5 {
    color: var(--light-gray-two);
  }
  .premium-tabledetail .featuretable-premium h5 {
    color: var(--light-blue);
  }
  .premium-tabledetail .row-header .featuretable-premium {
    background-color: var(--black);
  }
  .premium-tabledetail .check {
    filter: brightness(80%);
  }
  .premium-hero figure {
    filter: invert(100%) hue-rotate(180deg);
  }
  .premium-hero p, .hero-tile p, .quote-tile .quote, .stats-tile .post {
    color: var(--light-gray-two);
  }
  .package .plan {
    color: var(--light-gray);
  }
  /* secondary pages */
  .legacy-container {
    background-color: var(--black);
  }
  .legacy-content {
    background-color: var(--mid-gray);
  }
  .legacy-sidebar h3, .legacy-sidebar h5 {
    color: var(--white);
  }
  /* jobs page */
  .page-jobs .job-list ul {
    background-color: var(--mid-gray-two);
  }
  /* sponsor page */
  .page-sponsor > section:nth-child(2n), .page-sponsor .hero-bottom .copy {
    background-color: var(--mid-gray);
  }
  .page-sponsor section, h1, h2, h3, h4, h5, h6 {
    color: inherit;
  }
  .page-sponsor .partners .logo-list li {
    filter: invert(100%);
  }
  /* eoy section */
  .eoy--2018 .eoy-top-publishers {
    background-color: var(--mid-gray);
  }
  .eoy, .eoy--2018.eoy .eoy-publisher-link h3 {
    color: var(--light-gray-two);
  }
  .eoy--2018 .eoy-hero, .eoy .eoy-cheers {
    background-color: var(--mid-gray-two);
  }
  .eoy--2018.eoy .eoy-publisher-link h3:hover {
    color: var(--dark-gray);
  }
  /* blog */
  #sidebar .widget-title, .sidebar_content .post-title {
    color: var(--light-gray-two) !important;
  }
  #header, .site-footer, .sidebar_content #content {
    background-color: var(--mid-gray);
  }
  .post-title a:link, .post-title a:visited, .post-title a:hover,
  .post-title a:focus, a:link, a:visited {
    color: var(--med-blue);
  }
  /* developer page */
  .banner_preferencesapi a {
    background: var(--mid-gray-two) !important;
  }
  #container footer {
    background-color: var(--mid-gray);
    border-top-color: var(--icon-gray);
    border-bottom-color: var(--icon-gray);
  }
  #sidebar h5 {
    color: var(--mid-gray-three);
    text-shadow: none;
  }
  #sidebar a:hover {
    color: var(--light-blue);
  }
  img[src*="mask70.png"] {
    opacity: 0;
  }
  .welcome header, .welcome .description, #content.landing .learn_more h2,
  #content.landing .learn_more h4 {
    color: var(--icon-gray);
  }
  .code {
    color: var(--white);
    background-color: var(--black);
  }
  .codefont {
    color: var(--white);
  }
  /* publisher page */
  textarea[readonly="readonly"] {
    color: var(--light-gray-two);
    background-color: var(--black);
  }
  #pkt_btn_preview {
    overflow: hidden;
    background-color: var(--black);
  }
  .sidebar_content #content, .sidebar_content .content_section {
    color: var(--icon-gray);
    background-color: var(--mid-gray);
  }
  .integration h3 {
    color: var(--light-gray-two);
  }

sourceCode:
/* ==UserStyle==
@name         GitHub Compact Feed
@version      1.2.3
@description  Compact and hide details in the news feed
@namespace    StylishThemes
@author       StylishThemes
@homepageURL  https://github.com/StylishThemes/GitHub-Compact-Feed
@supportURL   https://github.com/StylishThemes/GitHub-Compact-Feed/issues
@updateURL    https://raw.githubusercontent.com/StylishThemes/GitHub-Compact-Feed/master/github-compact-feed.user.css
@license      CC-BY-SA-4.0
@advanced checkbox hide_comments "Hide All Comments" 0
@advanced checkbox hide_branches "Hide Branch Edits" 0
@advanced checkbox hide_closed   "Hide Closing"      0
@advanced checkbox hide_follows  "Hide Follows"      0
@advanced checkbox hide_forks    "Hide Forks"        0
@advanced checkbox hide_new      "Hide New Repos"    0
@advanced checkbox hide_open     "Hide Open/Reopen"  0
@advanced checkbox hide_stars    "Hide Stars"        0
@advanced checkbox hide_tags     "Hide Tags"         0
@advanced checkbox hide_pushes   "Hide Pushes"       0
@advanced checkbox hide_wiki     "Hide Wiki Events"  0
@preprocessor stylus
==/UserStyle== */
@-moz-document regexp("^https?:\\/\\/github\\.com(\\/)?$"), regexp("^https?:\\/\\/github\\.com\\/(orgs\\/.*\\/)?dashboard(\\/)?$") {
  if hide_comments {
    .news .issues_comment, .news .commit_comment {
      display: none !important;
    }
  }
  if hide_branches {
    .news .git-branch {
      display: none !important;
    }
  }
  if hide_closed {
    .news .issues_closed {
      display: none !important;
    }
  }
  if hide_follows {
    .news .follow {
      display: none !important;
    }
  }
  if hide_forks {
    .news .fork {
      display: none !important;
    }
  }
  if hide_stars {
    .news .watch_started {
      display: none !important;
    }
  }
  if hide_new {
    .news .repo, .news .create, .news .public {
      display: none !important;
    }
  }
  if hide_open {
    .news .issues_opened, .news .issues_reopened {
      display: none !important;
    }
  }
  if hide_pushes {
    .news .push {
      display: none !important;
    }
  }
  if hide_tags {
    .news .tag {
      display: none !important;
    }
  }
  if hide_wiki {
    .news .gollum {
      display: none !important;
    }
  }
  /* Hide expand button */
  .news div > .Details .js-details-target,
  /* Hide star repo details */
  .news .watch_started .Box,
  /* Hide text with 2+ stars */
  .news .watch_started .text-gray,
  /* .fork .border not using .Box yet */
  .news .fork .border, .news .fork .Box,
  .news .tag .Box, .news .git-branch .Box,
  /* Hide created repo update date */
  .news .repo .Box .f6,
  .news .public .border .f6,
  /* .public .border not using .Box yet */
  .news .public .Box .f6,
  .news .issues_comment .message, .news .commit_comment .message,
  /* Multiple comments */
  .news .issues_comment .Details .mt-2,
  /* Multiple comments */
  .news .commit_comment .Details .mt-2,
  /* Hide issue text */
  .news .issues_opened .Box div.lh-condensed,
  .news .issues_closed .Box div.lh-condensed, .news .issues_closed .Box .f6,
  .news .issues_closed .border .f6, .news .issues_reopened .Box div.lh-condensed,
  .news .issues_reopened .Box .f6,
  /* Hide PR diff stats */
  .news .border .diffstat, .news .follow .Box {
    display: none !important;
  }
  .news > div .Details:not(.Details--on) div.Details-content--hidden,
  /* Show multiple star repo names */
  .news .fork .Details .Box,
  .news .watch_started .Details .Box,
  .news .create .Details .d-flex .Box,
  /* Show star on created/public repos */
  .news .create .starring-container button[data-hydro-click] {
    display: block !important;
  }
  .news .watch_started .Details .d-flex .Box, .news .issues_opened .Box,
  .news .issues_closed .Box, .news .issues_reopened .Box {
    border: 0 !important;
    background: transparent !important;
    padding: 0 !important;
    margin-bottom: 0 !important;
  }
  /* Remove extra padding after comment is hidden */
  .news .p-3,
  /* Padding after PR diff-stat hidden */
  .news .border.p-3,
  .news .repo .Box.p-3, .news .public .border.p-3, .news .public .Box.p-3 {
    padding-bottom: 5px !important;
    padding-top: 5px !important;
  }
  .news p {
    margin-bottom: 0 !important;
  }
  /* Limit issue title width */
  .news .issues_opened .Box a, .news .issues_closed .Box a {
    display: inline-block !important;
    white-space: nowrap !important;
    max-width: 500px !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
  }
  /* Edited wiki, move "View the diff" inline */
  .news .gollum .text-gray .mt-2 {
    display: inline-block !important;
  }
  /* Move "# more commits" to bottom right */
  .news .push .link-gray {
    float: right !important;
    top: -2em !important;
    position: relative !important;
  }
}

